import React, { useState } from 'react';

const AnswerBoxes: React.FC = () => {
  const [question, setQuestion] = useState('');
  const [userAnswer, setUserAnswer] = useState('');
  const [correctAnswer, setCorrectAnswer] = useState('');

  const labelStyle = {
    fontWeight: 'bold',  // Make labels bold
    display: 'block',    // Make labels a block-level element for left alignment
    textAlign: 'left',   // Align text to the left
  };

  return (
    <div id="answerBoxes" className="rounded-box">
      <div style={{position:'relative'}}>
        <label style={{fontWeight: 'bold', position: 'absolute', left: '0', top: '0', width: '100px', display: 'block', textAlign: 'left'}}>Question:</label>
        <br />
        <textarea value={question} onChange={(e) => setQuestion(e.target.value)} />
      </div>
      <div>
        <label>Your Answer:</label>
        <br />
        <textarea value={userAnswer} onChange={(e) => setUserAnswer(e.target.value)} />
      </div>
      <div>
        <label>Correct Answer:</label>
        <br />
        <textarea value={correctAnswer} onChange={(e) => setCorrectAnswer(e.target.value)} />
      </div>
    </div>
  );
};

export default AnswerBoxes;
