import React, { useState } from 'react';

const UploadImg: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setFile(event.target.files[0]);
    }
  };

  return (
    <div id="uploadImage">
      {/* <button style={{display: 'block', width:'120px', height:'30px', onClick='document.getElementById('getFile').click()'}}>Choose Image</button> */}
      {/* <input type="file" id="getFile" onChange={handleFileChange} accept="image/png, image/jpeg" /> */}
      <label>
        Choose Image
      </label>
      <input type="file" id="getFile" onChange={handleFileChange} accept="image/png, image/jpeg" />
      <label>Choose Image</label>
    </div>
    
  );
};



export default UploadImg;

