const buttonStyle = {
  display: 'block',
  width: '200px', // Change the width to make it bigger
  height: '50px', // Change the height to make it bigger
  textAlign: 'center', // Center the text
  lineHeight: '50px', // Vertically center the text
  backgroundColor: 'lightblue', // Change the background color
  color: 'white', // Change the text color
  cursor: 'pointer', // Change the cursor on hover
};

return (
  <div id="uploadImage">
    <label htmlFor="getFile" style={buttonStyle} className="choose-image-button">
      Choose Image
    </label>
    <input type="file" id="getFile" onChange={handleFileChange} accept="image/png, image/jpeg" />
  </div>
);
};

export default UploadImg;
