import React, { useState } from 'react';

const UploadImg: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setFile(event.target.files[0]);
    }
  };


  const buttonStyle = {
    display: 'block',
    width: '200px', // Change the width to make it bigger
    height: '50px', // Change the height to make it bigger
    backgroundColor: 'lightblue', // Change the background color
    color: 'white', // Change the text color
    cursor: 'pointer', // Change the cursor on hover
  };

  const labelStyle = {
    // Use CSS to center text within the label
    textAlign: 'center',
  };

  return (
    <div id="uploadImage">
      <label htmlFor="getFile" className="choose-image-button" style={labelStyle}>
        <span style={buttonStyle}>Choose Image</span>
      </label>
      <input type="file" id="getFile" onChange={handleFileChange} accept="image/png, image/jpeg" />
    </div>
  );
};

export default UploadImg;

